(function(){tinymce.PluginManager.requireLangPack('nuxeoimageupload');tinymce.create('tinymce.plugins.NuxeoImageUploadPlugin',{init:function(ed,url){ed.addCommand('mceNuxeoImageUpload',function(){var template=new Array();var urlPlugin=nxContextPath+'/editor_image_upload.faces'+'?conversationId='+currentConversationId+'&conversationIsLongRunning=true';window.open(urlPlugin,'_blank','toolbar=0, scrollbars=1, location=0, statusbar=0, menubar=0, resizable=1, dependent=1, width=800, height=480');return true});ed.addButton('nuxeoimageupload',{title:'nuxeoimageupload.desc',cmd:'mceNuxeoImageUpload',image:url+'/images/icon.gif'})},getInfo:function(){return{longname:'Nuxeo',author:'Nuxeo',authorurl:'http://www.nuxeo.com',infourl:'http://www.nuxeo.com',version:"3.0"}}});tinymce.PluginManager.add('nuxeoimageupload',tinymce.plugins.NuxeoImageUploadPlugin)})();